ALTER TABLE SEGMENT_FLIGHT_ACTIVITY_FACT
ADD OVERNIGHT_STAYING NUMBER;


-- Update the OVERNIGHT_STAYING column with random data
UPDATE SEGMENT_FLIGHT_ACTIVITY_FACT
SET OVERNIGHT_STAYING = CASE 
                            WHEN MOD(DBMS_RANDOM.VALUE(1, 10), 10) > 3 THEN ROUND(DBMS_RANDOM.VALUE(1, 365))
                            ELSE NULL
                        END;